import React, { useState, useEffect } from 'react';
import './App.css';
import { Chart ,LineController, LineElement, PointElement} from 'chart.js';
Chart.register(LineController, LineElement, PointElement);



function TimelineChart() {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch('http://localhost:5000/api/data');
      const jsonData = await response.json();
      setData(jsonData);
    };

    fetchData();
  }, []);

  useEffect(() => {
    const ctx = document.getElementById('myChart').getContext('2d');
    const chart = new Chart(ctx, {
      type: 'line',
      data: {
        labels: data.map((item) => item.ts), // Extract timestamps for labels
        datasets: [{
          label: 'Vibration',
          data: data.map((item) => item.vibration), // Extract vibration values
          backgroundColor: 'rgba(54, 162, 235, 0.2)',
          borderColor: 'rgba(54, 162, 235, 1)',
          borderWidth: 1,
        }],
      },
      options: {
        // Customize chart options (scales, legend, etc.) as needed
      },
    });
  }, [data]);

  return (
    <div className="App">
      <h1>Machine Status</h1>
      <div className="chart-container">
        <canvas id="myChart" width="400" height="200"></canvas>
      </div>
    </div>
  );
}

export default TimelineChart;
